INSERT INTO `wp_posts` VALUES (248,1,'2024-06-26 19:06:06','2024-06-26 19:06:06','<!-- wp:embed {\"url\":\"https://vimeo.com/968201658?share=copy\",\"type\":\"video\",\"providerNameSlug\":\"vimeo\",\"responsive\":true,\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed is-type-video is-provider-vimeo wp-block-embed-vimeo wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://vimeo.com/968201658?share=copy\n</div></figure>\n<!-- /wp:embed -->\n\n<!-- wp:columns {\"verticalAlignment\":null,\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|40\",\"bottom\":\"var:preset|spacing|50\"}}}} -->\n<div class=\"wp-block-columns\" style=\"padding-top:var(--wp--preset--spacing--40);padding-bottom:var(--wp--preset--spacing--50)\"><!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"33.33%\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\" style=\"flex-basis:33.33%\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":1,\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"var:preset|spacing|30\",\"left\":\"var:preset|spacing|40\",\"right\":\"var:preset|spacing|10\"}}}} -->\n<h1 class=\"wp-block-heading has-text-align-left\" style=\"padding-top:0;padding-right:var(--wp--preset--spacing--10);padding-bottom:var(--wp--preset--spacing--30);padding-left:var(--wp--preset--spacing--40)\">CIXI - Active vehicles</h1>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"66.66%\",\"style\":{\"spacing\":{\"padding\":{\"right\":\"var:preset|spacing|40\",\"left\":\"var:preset|spacing|40\",\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\"}}}} -->\n<div class=\"wp-block-column\" style=\"padding-top:var(--wp--preset--spacing--10);padding-right:var(--wp--preset--spacing--40);padding-bottom:var(--wp--preset--spacing--10);padding-left:var(--wp--preset--spacing--40);flex-basis:66.66%\"><!-- wp:paragraph {\"align\":\"left\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\",\"left\":\"0\",\"right\":\"0\"}},\"typography\":{\"lineHeight\":\"2.1\"},\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|contrast\"}}}},\"textColor\":\"contrast\",\"fontSize\":\"small\"} -->\n<p class=\"has-text-align-left has-contrast-color has-text-color has-link-color has-small-font-size\" style=\"padding-top:var(--wp--preset--spacing--10);padding-right:0;padding-bottom:var(--wp--preset--spacing--10);padding-left:0;line-height:2.1\"><strong>Tools: </strong>Adobe Illustrator, After Effects<br><strong>Animation, sound and illustrations:  </strong>Pablo Fraustro<br><strong>Quality check: </strong>Carlos Torres<br><strong>Category:</strong> Commercial project</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">While working at <a href=\"http://cixi.life\" data-type=\"link\" data-id=\"cixi.life\">CIXI</a>, a French startup revolutionizing pedaling technology, I contributed to digital products and design studies for over two years. This video marked a pivotal moment as we prepared to introduce our innovation to the world. The project was meticulously crafted, with every word and visual element carefully designed to convey our message effectively.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|10\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--10)\"></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|40\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--40)\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\"}}}} -->\n<h2 class=\"wp-block-heading has-text-align-center\" style=\"padding-top:var(--wp--preset--spacing--10);padding-bottom:var(--wp--preset--spacing--10)\">Process</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"spacing\":{\"padding\":{\"right\":\"var:preset|spacing|60\",\"left\":\"var:preset|spacing|60\"}}}} -->\n<p class=\"has-text-align-center\" style=\"padding-right:var(--wp--preset--spacing--60);padding-left:var(--wp--preset--spacing--60)\">We began by defining all the visuals to align with the voiceover, focusing on themes of vision and active mobility. This led to the creation of diverse and imaginative vehicles and characters pedaling. Technically, most elements were created in Adobe Illustrator and then imported into After Effects.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:spacer {\"height\":\"30px\"} -->\n<div style=\"height:30px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:modula/gallery {\"id\":168,\"images\":[{\"id\":\"170\",\"alt\":\"Vehicle styleframe\",\"title\":\"Vehicle styleframe\",\"description\":\"\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":576,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/3-1024x576.png\",\"data-width\":7,\"data-height\":6},{\"id\":\"169\",\"alt\":\"Vehicle styleframe\",\"title\":\"Vehicle styleframe\",\"description\":\"\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":576,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/2-1024x576.png\",\"data-width\":5,\"data-height\":3},{\"id\":\"171\",\"alt\":\"Vehicle styleframe\",\"title\":\"Vehicle styleframe\",\"description\":\"\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":576,\"togglelightbox\":0,\"src\":\"http://localhost:1