INSERT INTO `wp_posts` VALUES (393,1,'2024-06-27 16:54:08','2024-06-27 16:54:08','<!-- wp:columns {\"style\":{\"spacing\":{\"padding\":{\"bottom\":\"var:preset|spacing|50\"}}}} -->\n<div class=\"wp-block-columns\" style=\"padding-bottom:var(--wp--preset--spacing--50)\"><!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"50%\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"0\",\"right\":\"var:preset|spacing|10\",\"left\":\"var:preset|spacing|10\"}}},\"className\":\"top-video\",\"layout\":{\"type\":\"constrained\",\"justifyContent\":\"left\",\"contentSize\":\"\"}} -->\n<div class=\"wp-block-column is-vertically-aligned-center top-video\" style=\"padding-top:0;padding-right:var(--wp--preset--spacing--10);padding-bottom:0;padding-left:var(--wp--preset--spacing--10);flex-basis:50%\"><!-- wp:embed {\"url\":\"https://vimeo.com/960509618?share=copy\",\"type\":\"video\",\"providerNameSlug\":\"vimeo\",\"responsive\":true,\"className\":\"wp-embed-aspect-1-1 wp-has-aspect-ratio\",\"style\":{\"spacing\":{\"margin\":{\"right\":\"0\",\"left\":\"0\"}}}} -->\n<figure class=\"wp-block-embed is-type-video is-provider-vimeo wp-block-embed-vimeo wp-embed-aspect-1-1 wp-has-aspect-ratio\" style=\"margin-right:0;margin-left:0\"><div class=\"wp-block-embed__wrapper\">\nhttps://vimeo.com/960509618?share=copy\n</div></figure>\n<!-- /wp:embed --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"50%\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"2rem\",\"bottom\":\"var:preset|spacing|10\",\"left\":\"var:preset|spacing|30\",\"right\":\"0\"}}}} -->\n<div class=\"wp-block-column is-vertically-aligned-center\" style=\"padding-top:2rem;padding-right:0;padding-bottom:var(--wp--preset--spacing--10);padding-left:var(--wp--preset--spacing--30);flex-basis:50%\"><!-- wp:heading {\"level\":1,\"style\":{\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"var:preset|spacing|20\"}}}} -->\n<h1 class=\"wp-block-heading\" style=\"padding-top:0;padding-bottom:var(--wp--preset--spacing--20)\">Animated dunk</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"spacing\":{\"padding\":{\"top\":\"0px\",\"bottom\":\"0px\"}},\"typography\":{\"lineHeight\":\"2\"},\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|contrast\"}}}},\"textColor\":\"contrast\",\"fontSize\":\"small\"} -->\n<p class=\"has-contrast-color has-text-color has-link-color has-small-font-size\" style=\"padding-top:0px;padding-bottom:0px;line-height:2\"><br><strong>Tools: </strong>Procreate dreams<br><strong>Category: </strong>Personal project</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Floating through the skies, papa jump defying physics. Inspired by the fluid motion of a basketball dunk, I aimed to capture the sense of weightlessness and agility. This project showcases an exaggerated, elongated jump, highlighting the dynamic movement and impressive height achieved during the dunk.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|40\",\"bottom\":\"var:preset|spacing|30\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--40);padding-bottom:var(--wp--preset--spacing--30)\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|10\",\"bottom\":\"var:preset|spacing|10\"}}}} -->\n<h2 class=\"wp-block-heading has-text-align-center\" style=\"padding-top:var(--wp--preset--spacing--10);padding-bottom:var(--wp--preset--spacing--10)\">Process</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"spacing\":{\"padding\":{\"right\":\"var:preset|spacing|60\",\"left\":\"var:preset|spacing|60\"}}}} -->\n<p class=\"has-text-align-center\" style=\"padding-right:var(--wp--preset--spacing--60);padding-left:var(--wp--preset--spacing--60)\">Following <a href=\"https://www.domestika.org/en/courses/235-2d-animation-with-photoshop-draw-camera-action\">Numencaniqs 2D camera course</a>, which focuses on framing, camera movements, and spacing, I planned the animation beforehand. This preparation allowed me to quickly map out the sequence without overthinking each position, effectively exaggerating the movement through camera angles, spacing, and timing.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:modula/gallery {\"id\":125,\"images\":[{\"id\":\"117\",\"alt\":\"Sketches\",\"title\":\"Sketches\",\"description\":\"Initial sketches\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":1024,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/Sketches-1024x1024.png\",\"data-width\":2,\"data-height\":2},{\"id\":\"104\",\"alt\":\"Rotating character head\",\"title\":\"character head\",\"description\":\"character turnaround\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":600,\"height\":600,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/head.gif\",\"data-width\":2,\"data-height\":2},{\"id\":\"146\",\"alt\":\"Character\",\"title\":\"characterdesign\",\"description\":\"Character\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":1024,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/characterdesign-1024x1024.png\",\"data-width\":4,\"data-height\":4},{\"id\":\"118\",\"alt\":\"Planning\",\"title\":\"Planning\",\"description\":\"Planning\",\"halign\":\"center\",\"valign\":\"middle\",\"link\":\"\",\"target\":0,\"width\":1024,\"height\":1024,\"togglelightbox\":0,\"src\":\"http://localhost:10003/wp-content/uploads/2024/06/Planning-1024x1024.png\",\"data-width\":4,\"data-height\":3},{\"id\":\"86\",\"alt\":\"Character poses\",\"title\":\"character poses\",\"description\":\"Exploring character poses\",\"halign\":\